mapper.select:
SELECT T1.*,T2.* FROM( SELECT B.id, B.dept_nm as deptNm, B.p_title as pTitle, B.p_type as pType, B.task_contents as taskContents, B.customer, B.area, B.working_form as workingForm, B.mod_id as modId, B.reg_id as regId, CASE TO_CHAR(B.reg_date,'YYYY/MM/DD') WHEN TO_CHAR(SYSDATE,'YYYY/MM/DD') THEN TO_CHAR(SYSDATE,'HH24:MI') ELSE TO_CHAR(B.reg_date,'YYYY/MM/DD') END regDate, B.rnum as num FROM( SELECT ROWNUM AS rnum,a.* FROM ( SELECT * FROM todo WHERE 1=1 --검색조건
ORDER BY reg_date desc )A WHERE ROWNUM <= (#{pageSize} *(#{pageNum} - 1)+#{pageSize}) )B WHERE rnum >= (#{pageSize}*(#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM todo WHERE 1=1 --검색조건
)T2

mapper.insert:
INSERT INTO todo ( id, dept_nm, p_title, p_type, customer, task_contents, area, working_form, reg_id, mod_id, mod_date
) VALUES ( #{id }, #{deptNm }, #{pTitle }, #{pType }, #{customer }, #{taskContents }, #{area }, #{workingForm }, #{regId }, #{modId }, #{modDate }
)
